package niuke;

public class ty4 {

    public static void main(String[] args) {
        ty4 t4 = new ty4();
        System.out.println(t4.minOperations(""));
    }

    public int minOperations (String str) {
        // write code here
        int oneToZero = helper(str,'0');
        int zeroToOne = helper(str, '1');
        return Math.min(oneToZero,zeroToOne);
    }

    public static int helper(String s, char tag) {
        int count = 0;
        for (int i = 0; i < s.length() ; i++) {
            char c = s.charAt(i);
            if (i == s.length() - 1 && c == tag){
                count++;
            }
            if (c == tag) {
                count++;
                i++;
            }
        }
        return count;
    }
}
